operation.add:
			/* Admin Side  menu */						
			$adminmenus = array();		
			if ($this->user->hasPermission('access', 'extension/payment/razorpay')) {	
				$adminmenus[] = array(
					'name'	   => 'Plans',
					'href'     => $this->url->link('extension/payment/razorpay/getPlan', 'user_token=' . $this->session->data['user_token'], true),
					'children' => array()		
				);					

			}	
            if ($this->user->hasPermission('access', 'extension/payment/razorpay')) {		
				$adminmenus[] = array(
					'name'	   => 'Subscriptions',
					'href'     => $this->url->link('extension/payment/razorpay/getSubscription', 'user_token=' . $this->session->data['user_token'], true),
					'children' => array()		
				);					

			}				
			if ($adminmenus) {					
				$data['menus'][] = array(
					'id'       => 'menu-megafooter',
					'icon'	   => 'fa fa-share-alt fa-fw', 
					'name'	   => 'Razorpay Subscription',
					'href'     => '',
					'children' => $adminmenus
				);		
			}
			/* Admin menu */
