operation.add:
			/* Admin Side  menu */						
			$adminmenus = array();

			if ($this->user->hasPermission('access', 'marketplace/extension')) {		
				$adminmenus[] = array(
					'name'	   => 'Plans',
					'href'     => $this->url->link('extension/payment/razorpay_subscription/plan_list', 'user_token=' . $this->session->data['user_token'], true),
					'children' => array()		
				);					

			}	
            if ($this->user->hasPermission('access', 'marketplace/extension')) {		
				$adminmenus[] = array(
					'name'	   => 'Subscriptions',
					'href'     => $this->url->link('extension/payment/razorpay_subscription', 'user_token=' . $this->session->data['user_token'], true),
					'children' => array()		
				);					

			}	

			if ($adminmenus) {					
				$data['menus'][] = array(
					'id'       => 'menu-megafooter',
					'icon'	   => 'fa fa-share-alt fa-fw', 
					'name'	   => 'Razorpay Subscription',
					'href'     => '',
					'children' => $adminmenus
				);		
			}

			/* Admin menu */
